document.addEventListener("DOMContentLoaded",()=>{const c=["step-1","step-2","step-3"];let e=0;const t=n=>{c.forEach((d,o)=>{const s=document.getElementById(d);o===n?s.classList.add("active"):s.classList.remove("active")})};document.getElementById("next-1").addEventListener("click",()=>{e=1,t(e)}),document.getElementById("prev-2").addEventListener("click",()=>{e=0,t(e)}),document.getElementById("next-2").addEventListener("click",()=>{e=2,t(e)}),document.getElementById("prev-3").addEventListener("click",()=>{e=1,t(e)}),document.getElementById("form-step-3").addEventListener("submit",n=>{n.preventDefault(),alert("帳號創建完成！")})});
